import streamlit as st
import json
import pandas as pd
df=pd.read_csv("dogs/psi.csv")


st.set_page_config(layout="wide",page_title="Atlas plemen psů",page_icon="data:image/jpeg;base64,")

if "df" not in st.session_state:
    st.session_state["df"]=df

st.markdown("## Atlas plemen psů")

st.image("https://www.kindpng.com/picc/m/353-3530606_dog-training-banner-hd-png-download.png")


nazev_psa=st.text_input("Zadejte plemeno", key="plemeno")
Tlacitko_vyhledejte=st.button("Vyhledejte", key="tlacitko_vyhledejte")
if Tlacitko_vyhledejte:
     podmínka=df["Název"].str.contains(nazev_psa,case=False)
     st.session_state["df"]=df[podmínka]
     

for index, zvíře in st.session_state["df"].iterrows():
    with st.container(border=True):
         st.subheader(zvíře["Název"])
         col1, col2,col3,col4,col5=st.columns(5)
         with col1:
            col1_2, col1_3=st.columns(2)
            with col1_2:
                st.badge("Velikost", color="violet")
            with col1_3:
                st.write(f"{zvíře['Velikost']})")
             
         with col2:
             col2_3,col2_4=st.columns(2)
             with col2_3:
                 st.badge("Hmotnost", color="red")
             with col2_4:
                 st.write(f"{zvíře['Hmotnost']}")
         with col3: 
             col3_3,col3_4=st.columns(2)
             with col3_3:
                 st.badge("Země původu", color="green")
             with col3_4:
                 st.write(f"{zvíře['Země původu']}")
         with col4:
              col4_1,col4_2=st.columns(2)   
              with col4_1:
                 st.badge("Zaměnitelné plemeno", color="orange")
              with col4_2:
                 st.write(f"{zvíře.get('Zaměnitelné plemeno', "Žádné")}")
         with col5:
              col5_1,col5_2=st.columns(2)
              with col5_1:
                 st.badge("Povaha", color="yellow")
              with col5_2:
                 st.write(f":{zvíře['Povaha']}") 
         st.image(zvíře["Obrázek"])
        
         with st.expander("Zobrazit více"):
              st.badge("Charakter", color="blue")
              st.write(f"{zvíře["Charakter"]}")

              st.badge("Původ", color="blue")
              st.write(f"{zvíře["Původ"]}")
            
              st.badge("Osrstění", color="blue")
              st.write(f"{zvíře["Osrstění"]}")
         
              st.badge("Zbarvení srsti", color="blue")
              st.write(f"{zvíře["Zbarvení srsti"]}")

              st.badge("Celkový dojem", color="blue")
              st.write(f"{zvíře["Celkový dojem"]}")
             
              st.badge("Název v zemi vzniku", color="blue")
              st.write(f"{zvíře["Název v zemi vzniku"]}")
              
              st.badge("Kategorie dle FCI", color="blue")
              st.write(f"{zvíře["Kategorie dle FCI"]}")

              